pragma solidity ^0.4.2;

contract NoteData{

    //恋爱记录
    struct Note{
    bytes32    uuid;
    address sender;
    string  content;
    uint32  feelings;   //心情
    string  pictures;   //上传图片的ipfs hash json数组
    string  ext_info;   //扩展字段 格式为json对象
    uint64  timestamp;
    }

    //记录回复
    struct Reply{
    bytes32    note_uuid;
    address replyer;
    string  content;
    string  ext_info;   //扩展字段 格式为json对象
    uint64  timestamp;
    }

    mapping(bytes32=>Note[])   internal  notes;
    mapping(bytes32=>Reply[])    internal  replies;


    function setNote(bytes32 lovers_id,bytes32 uuid,address sender,string content,uint32 feelings,string pictures,string ext_info){
        notes[lovers_id].push(Note(uuid,sender,content,feelings,pictures,ext_info,uint64(now)));
    }

    function  getNote(bytes32 lovers_id,uint index)public view returns(bytes32,address,string,uint32,string,string,uint64,uint){
        Note memory n=notes[lovers_id][index];
        uint reply_length=replies[n.uuid].length;
        return (n.uuid,n.sender,n.content,n.feelings,n.pictures,n.ext_info,n.timestamp,reply_length);
    }

    function  countNotes(bytes32 lovers_id)public view returns(uint){
        return notes[lovers_id].length;
    }

    function setReply(bytes32 note_uuid,address replyer,string content,string ext_info){
        replies[note_uuid].push(Reply(note_uuid,msg.sender,content,ext_info,uint64(now)));
    }

    function getReply(bytes32 note_uuid,uint index)public view returns(bytes32,address,string,string,uint64){
        Reply memory r=replies[note_uuid][index];
        return (r.note_uuid,r.replyer,r.content,r.ext_info,r.timestamp);
    }

    function  countReplies(bytes32 note_uuid)public view returns(uint){
        return replies[note_uuid].length;
    }


}
